mapper.select:
SELECT ta.id AS id, ta.announcement_title AS announcementTitle, ta.annex_name AS annexName, ta.view_num AS viewNum, ta.publish_range AS publishRange, ta.create_time AS createTime, ta.create_by AS createBy, ta.user_name AS creator, ta.announcement_content AS announcementContent, ta.annex_url AS annexUrl, ta.message AS message FROM t_announcement ta where 1=1
and ta.id=#{id}
and ta.announcement_title=#{announcementTitle}

mapper.update:
		UPDATE t_announcement
		SET view_num = #{viewNum,jdbcType=INTEGER}
		WHERE 1=1
		
			and id = #{id,jdbcType=VARCHAR}
		
		
			and announcement_title=#{announcementTitle}

mapper.select:
		select t.PUBLISH_RANGE AS areaName from t_announcement t where 1=1
		
			and t.id=#{id}
		
		
			and t.announcement_title=#{announcementTitle}
