mapper.select:
SELECT ta.id AS id, ta.REGULATION_TITLE AS regulationTitle, ta.CREATOR AS creator, ta.ORGANIZATION AS organization, ta.CREATE_TIME AS createTime, ta.annex_name AS annexName, ta.annex_url AS annexUrl FROM t_regulation ta where 1=1
and ta.id=#{id}
and ta.REGULATION_TITLE=#{regulationTitle}

mapper.update:
		UPDATE t_announcement
		SET view_num = #{viewNum,jdbcType=INTEGER}
		WHERE 1=1
		
			and id = #{id,jdbcType=VARCHAR}
		
		
			and announcement_title=#{announcementTitle}

mapper.select:
		select t.JRJGMC AS areaName from t_announcement t where 1=1
		
			and t.id=#{id}
		
		
			and t.announcement_title=#{announcementTitle}

SELECT T.br_no "id", T.jrjgmc "name", T.jgjb "level", T.sjgljgnbjgh "pId", T.br_no "id" FROM HBJYODS.jrjgxx T where 1=1
and T.BR_NO = #{publishRange}
and T.jgjb ='2'

SELECT T.br_no "id", T.jrjgmc "name", T.jgjb "level", T.sjgljgnbjgh "pId", T.br_no "id" FROM HBJYODS.jrjgxx T where 1=1
and T.BR_NO = #{publishRange}
and T.jgjb ='3'
