select b.sjrq "sjrq", b.wjbm "wjbm", b.wjmc "wjmc", b.bszt "bszt", b.code "code", b.nbjgh "nbjgh", b.username "username", b.ymdhms "drrq", b.content "sbsjmx", um.id "id", um.name "name", um.teleph "teleph", um.ssbm "ssbm", um.ssjg "ssjg", um.ip "ip", um.dwbm "dwbm", um.smsnotice "smsnotice" from BSGL b inner join user_manager um on b.username = um.yh_name
